#用户注册
CREATE TABLE moli_login(
  id INT PRIMARY KEY AUTO_INCREMENT,
  name VARCHAR(25),
  uname VARCHAR(25),
  cm VARCHAR(60),
  pwd  VARCHAR(32),
  phone VARCHAR(11)
);

#新闻内容
CREATE TABLE moli_tent(
  id INT PRIMARY KEY AUTO_INCREMENT,
  lid INT,
  uname VARCHAR(10),
  title VARCHAR(100),
  mg VARCHAR(55),
  ctime DATETIME,
  nid INT
);

#小视频
CREATE TABLE moli_video(
  id INT PRIMARY KEY AUTO_INCREMENT,
  uname VARCHAR(10),
  deo VARCHAR(55),
  ment VARCHAR(10),
  ctime DATETIME,
  nid INT
);

#国风内容
CREATE TABLE moli_toms(
  id INT PRIMARY KEY AUTO_INCREMENT,
  lid INT,
  uname VARCHAR(10),
  title VARCHAR(100),
  jmg VARCHAR(55),
  cmg VARCHAR(55),
  vmg VARCHAR(55),
  ment VARCHAR(10),
  ctime DATETIME,
  ind INT
);

#新闻内容
CREATE TABLE moli_nba(
  id INT PRIMARY KEY AUTO_INCREMENT,
  lid INT,
  uname VARCHAR(10),
  title VARCHAR(100),
  jmg VARCHAR(55),
  ctime DATETIME,
  ment VARCHAR(10),
  nid INT
);
#详情
CREATE TABLE moli_details(
  id INT PRIMARY KEY AUTO_INCREMENT,
  lid INT,
  title VARCHAR(100),
  aont VARCHAR(300),
  bont VARCHAR(1000),
  cont VARCHAR(1000),
  dont VARCHAR(1000),
  eont VARCHAR(1000),
  fmg VARCHAR(55),
  bmg VARCHAR(55),
  cmg VARCHAR(55),
  dmg VARCHAR(55),
  emg VARCHAR(55),
  ctime DATETIME
);

#评论
CREATE TABLE moli_critic(
  id INT PRIMARY KEY AUTO_INCREMENT,
  lid INT,
  uname VARCHAR(10),
  title VARCHAR(100),
  ctime DATETIME
);
